#1.Combien y a-t-il de journalistes dans ce array ?
def countNumberOfJournalists(array)
  puts"1. Combien y a-t-il de journalistes dans ce array ?"
  puts"Il y a #{array.count} journalistes dans cet array."
  puts('')
end

#2. Combien d'handle contiennent un numéro ?
def countHandleWithANumber(array)
  puts"2. Combien d'handle contiennent un numéro ?"
  i = 0
  array.each do |handle|
    if handle =~ /\d/ 
      i = i + 1
    end
  end 
  puts"Il y a #{i} handles qui contiennent un numéro."
  puts""
end

#3. Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?
def countHandlewithAude(array)
  puts("3.Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite?")
  i = 0
  array.each do |handle|
    if /aude/.match(handle.downcase)
      i = i + 1
    end
  end 
  puts"Il y a #{i} handles qui contiennent 'Aude'."
  puts""
end

#4. Combien commencent par une majuscule (première lettre juste après le @) ?
def countStartWithUpperCase(array)
  puts("4. Combien commencent par une majuscule")
  i = 0
  array.each do |handle|
    firstLetter = handle[1]
    if /\p{Lu}/.match(firstLetter)
      i = i + 1
    end
  end 
  puts"Il y a #{i} pseudos qui commencent par une majuscule."
  puts""
end

#5. Combien contiennent au moins une majuscule ?
def countWithUpperCase(array)
  puts("5. Combien contiennent au moins une majuscule ?")
  i = 0
  array.each do |handle|
    handle.length.times do |l|
     if /\p{Lu}/.match(handle[l])
        handle = handle.downcase
        i = i + 1
     end
    end
  end 
  puts"Il y a #{i} pseudos qui contiennent au moins une majuscule."
  puts""
end

#6. Combien y a-t-il de underscore _ dans tous les pseudos confondus ?
def countUnderscores(array)
  puts("6. Combien y a-t-il de underscore _ dans tous les pseudos confondus")
  i = 0
  array.each do |handle|
    handle.length.times do |l|
     if /_/.match(handle[l])
        i = i + 1
     end
    end
  end 
  puts"Il y a #{i} underscores."
  puts""
end

#7. Trie la liste de handle par ordre alphabétique.
def sortByAlpabeticalOrder(array)
  puts('7. Trie la liste de handle par ordre alphabétique.')
  i = 0
  newArray = []
  array.each do |handle|
    newArray << handle.downcase
    newArray = newArray.sort
  end
  puts(newArray)
  puts('')
end

#8. Quels sont les 50 handles les plus courts de ce array ?
def count50minimum(array)
  array = array.sort_by {|x| x.length}
  newArray = []
  50.times do |i|
    newArray << array[i]
  end
puts (newArray)
end


#9. Quelle est la position dans l'array de la personne @epenser ?
def findPosition(array)
  puts("9. Quelle est la position de la personne @epenser ?")
  position = array.index("@epenser")
  position = position + 1
  puts"Il est à la position #{position}."
  puts""
end


